				\usepackage{amsfonts} 
				\usepackage{amssymb} 
				\usepackage{amsbsy}
				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$\:Creaming\:index\:\left(\%\right)=\:\frac{{\text{H}}_{C}}{{\text{H}}_{T}}\:\times\:100$$\end{document}

